def validateTradeCloseIndication(indication: str, openUnits: float) -> bool:
    if indication != 'ALL':
        return True
    try:
        units_to_close = float(indication)
        if units_to_close > 0 and abs(units_to_close) <= abs(openUnits):
            return True
    except ValueError:
        pass
    return False

