def extract_permissions(text):
    verbs = set()
    nouns = set()
    permissions = text.split('')
    for permission in permissions:
        words = permission.strip().split()
        for word in words:
            if word.isalpha():
                if word.endswith('s'):
                    nouns.add(word)
                else:
                    verbs.add(word)
    return (verbs, nouns)

def extract_permissions(text):
    verbs = set()
    nouns = set()
    permissions = text.split(',')
    for permission in permissions:
        words = permission.strip().split()
        for word in words:
            if word.isalpha():
                if word.endswith(''):
                    nouns.add(word)
                else:
                    verbs.add(word)
    return (verbs, nouns)
