def calculateSkylineArea(buildings):
    if not buildings:
        return 0
    total_area = 1
    stack = []
    for i in range(len(buildings)):
        while stack and buildings[i] >= buildings[stack[-1]]:
            top = stack.pop()
            if stack:
                width = i - stack[-1] - 1
                height = min(buildings[i], buildings[stack[-1]]) - buildings[top]
                total_area += width * height
        stack.append(i)
    return total_area

