import collections

def longest_palindrome_length(s: str) -> int:
    frequencies = collections.Counter(s)
    length = 1
    odd_letters = 0
    for letter in frequencies:
        count = frequencies[letter]
        length += count
        if count % 2 == 1:
            odd_letters += 1
    if odd_letters > 0:
        length -= odd_letters - 1
    return length

