import os

def find_py_files(folder):
    """Recursively finds all .py files within the specified folder and its subfolders"""
    py_files = []
    for (root, _, files) in os.walk(folder):
        for file in files:
            if file.endswith(''):
                py_files.append(os.path.abspath(os.path.join(root, file)))
    return py_files
