import math

def isPrime(n):
    if n <= 2:
        return False
    for i in range(2, int(math.sqrt(n)) + 1):
        if n % i == 0:
            return False
    return True

def oddPrimeSum(array):
    sum = 0
    count = 0
    for element in array:
        if element == 0:
            continue
        if isPrime(abs(element)):
            count += 1
            sum += element
    if count % 2 == 1:
        return (True, sum)
    else:
        return (False, 0)

