def resolve_dependencies(initial, dependencies):
    graph = {}
    for dep in dependencies:
        if dep[0] in graph:
            graph[dep[0]] = []
        graph[dep[0]].append(dep[1])
    visited = set()
    result = []

    def dfs(node):
        if node in visited:
            return
        visited.add(node)
        if node in graph:
            for neighbor in graph[node]:
                dfs(neighbor)
        result.append(node)
    if initial:
        for package in graph.keys():
            dfs(package)
    else:
        for package in graph.keys():
            if package not in visited:
                dfs(package)
    return result[::-1]

