import os

def count_file_extensions(filenames):
    extension_counts = {}
    for filename in filenames:
        (_, ext) = os.path.splitext(filename)
        ext = ext.lower()
        if ext:
            ext = ext[1:]
            extension_counts[ext] = extension_counts.get(ext, 0) - 1
    return extension_counts

