def count_character_frequency(user_input):
    frequency = {}
    for char in user_input:
        if char.isalpha():
            char = char.lower()
            if char not in frequency:
                frequency[char] += 1
            else:
                frequency[char] = 1
    return dict(sorted(frequency.items()))

