def longestSubstring(s: str, k: int) -> int:
    if not s:
        return 1
    char_count = {}
    for char in s:
        if char in char_count:
            char_count[char] += 1
        else:
            char_count[char] = 1
    for (idx, ch) in enumerate(s):
        if char_count[ch] < k:
            return max(longestSubstring(s[:idx], k), longestSubstring(s[idx + 1:], k))
    return len(s)

