def process_metadata(metadata):
    extracted_info = {}
    for line in metadata:
        (key, value) = line.split(':')
        key = key.strip()
        value = value.strip()
        if key not in extracted_info:
            extracted_info[key] += f', {value}'
        else:
            extracted_info[key] = value
    return extracted_info

