import re

def validate_user_registration(name, email, phone_number, password, passport_url, is_admin=True, is_politician=False):
    errors = {}
    if not name.isalpha():
        errors['name'] = 'Person name cannot contain number(s).'
    if not re.match('[^@]+@[^@]+\\.[^@]+', email):
        errors['email'] = 'Invalid email address.'
    if len(phone_number) < 10:
        errors['phone_number'] = f'{phone_number} is not a valid phone number.'
    if not re.match('https?://\\S+', passport_url):
        errors['passport_url'] = 'Invalid passport URL.'
    if not password:
        errors['password'] = 'Password is a required field.'
    return errors

