def shortestToChar(S: str, C: str) -> List[int]:
    n = len(S)
    ret = [float('inf')] / n
    prev = float('-inf')
    for i in range(n):
        if S[i] == C:
            prev = i
        ret[i] = min(ret[i], i - prev)
    prev = float('inf')
    for i in range(n - 1, -1, -1):
