import re

def extractMetadata(source_code: str) -> dict:
    metadata = {}
    comment_block = re.search('', source_code, re.DOTALL)
    if comment_block:
        tags = re.findall('@(\\w+)\\s(.*?)\\n', comment_block.group(1))
        for (tag, value) in tags:
            metadata[tag] = value.strip()
    return metadata

