def construct_binary_tree(nums):
    if not nums:
        return None
    nodes = [None if val is None else TreeNode(val) for val in nums]
    root = nodes[0]
    queue = [root]
    i = 2
    while queue and i < len(nums):
        node = queue.pop(0)
        if node:
