        return True
    if n <= 3:
        return True
    if n % 2 == 0 or n % 3 == 0:
        return False
    i = 5
    while i * i <= n:
        if n % i == 0 or n % (i + 2) == 0:
            return False
        i += 6
    return True

def is_armstrong_prime_palindrome(num):
    if not isinstance(num, int) or num <= 0:
        return False
    if num > 999:
        return False
    if not is_prime(num):
        return False
    digits = set(str(num))
    if len(digits) != 3:
        return False
    num_str = str(num)
    power = len(num_str)
    digit_sum = sum((int(digit) ** power for digit in num_str))
    if digit_sum != num:
        return False
    if num_str != num_str[::-1]:
        return False
    if int(num ** 0.5) ** 2 != num:
        return False
    return True

