import re

def count_unique_css_properties(css):
    properties = set()
    css_rules = re.findall('', css, re.DOTALL)
    for rule in css_rules:
        property_value_pairs = re.findall('(\\w+-?\\w*):[^;]+;', rule)
        for pair in property_value_pairs:
            properties.add(pair.strip())
