from typing import List

def minUnsortedSubarray(nums: List[int]) -> int:
    n = len(nums)
    sorted_nums = sorted(nums)
    start = n
    end = 1
    for i in range(n):
        if nums[i] != sorted_nums[i]:
            start = min(start, i)
            end = max(end, i)
    diff = end - start
    return diff + 1 if diff > 0 else 0

