def print_multiplication_table(n, m):
    """"""
    if not isinstance(n, int) or not isinstance(m, int) or n <= 0 or (m <= 0) or (m > n):
        print('Invalid input')
        return
    for i in range(1, m + 1):
        row = []
        for j in range(1, n + 1):
            if j <= i:
                row.append(str(i * j))
            else:
                row.append('')
        print('\t'.join(row))
