def get_summary(text, max_length):
    """"""
    if len(text) <= max_length:
        return text
    else:
        summary = text[:max_length]
        last_period = summary.rfind('.')
        if last_period != -1 and last_period > max_length - 20:
            return summary[:last_period + 1]
        else:
            return summary + '...'
