import re

def count_unique_words(input_string):
    word_counts = {}
    words = re.findall('', input_string.lower())
    for word in words:
        if word in word_counts:
            word_counts[word] += 1
        else:
            word_counts[word] = 1
    return dict(sorted(word_counts.items(), key=lambda item: item[1], reverse=True))

