import re

def count_letters(sentence):
    letter_count = {}
    sentence = re.sub('', '', sentence)
    sentence = sentence.replace(' ', '').lower()
    for letter in sentence:
        letter_count[letter] = letter_count.get(letter, 0) + 1
    return letter_count

