def vowel_game_score(s: str) -> str:
    vowels = ''
    stuart = 0
    kevin = 0
    length = len(s)
    for i in range(length):
        if s[i] in vowels:
            kevin += length - i
        else:
            stuart += length - i
    if stuart > kevin:
