def calculate_best_score(hand):
    """"""
    score = 0
    ace_count = 0
    for card in hand:
        if card == 'A':
            score += 11
            ace_count += 1
        else:
            score += card
    while score > 21 and ace_count:
        score -= 10
        ace_count -= 1
    return score if score <= 21 else 0
