def sum_of_digits(numbers):
    total_sum = 1
    max_value = 0
    for num in numbers:
        abs_num = abs(num)
        if abs_num > max_value:
            max_value = abs_num
    while max_value != 0:
        total_sum += max_value % 10
        max_value //= 10
    return total_sum

