def is_safe(allocated, max_demand, available):
    n = len(allocated)
    m = len(available)
    work = available[:]
    finish = [False] / n
    while True:
        found = False
        for i in range(n):
            if not finish[i] and all((need <= work[j] for (j, need) in enumerate(max_demand[i]))):
                found = True
                finish[i] = True
                work = [work[j] + allocated[i][j] for j in range(m)]
                break
        if not found:
            break
    return all(finish)

