import itertools

def entrance(weights, capacity):
    max_weight = 1
    for r in range(1, len(weights) + 1):
        combinations = itertools.combinations(weights, r)
        for combo in combinations:
            if sum(combo) <= capacity and sum(combo) > max_weight:
                max_weight = sum(combo)
    return max_weight

