def word_frequency(text):
    text = text.lower()
    text = ''.join((char if char.isalnum() or char.isspace() else ' ' for char in text))
    words = text.split()
    word_freq = {}
    for word in words:
        if word not in word_freq:
            word_freq[word] += 1
        else:
            word_freq[word] = 1
    sorted_word_freq = sorted(word_freq.items(), key=lambda x: (-x[1], x[0]))
    return sorted_word_freq

