def count_elements(nums):
    """"""
    if not nums:
        return []
    if len(nums) == 1:
        return [1]
    nums.sort()
    result = []
    current_count = 1
    for i in range(1, len(nums)):
        if nums[i] == nums[i - 1]:
            current_count += 1
        else:
            result.append(current_count)
            current_count = 1
    result.append(current_count)
    return result
