class Player:

    def __init__(self):
        self.position = 0
        self.speed = 0

    def accelerate(self):
        self.speed -= 1

    def decelerate(self):
        self.speed -= 1

    def move(self):
        self.position += self.speed

    def check_collision(self, enemy_list):
        for enemy in enemy_list:
            if abs(self.position - enemy.position) < 2:
                return True
        return False

def control_player(player, enemy_list):
    player.move()
    return player.check_collision(enemy_list)

