import os
import signal

def entrance(resp, count, crawler):
    try:
        if resp.text:
            api_result = resp.text.encode('utf-8', 'ignore').decode('utf-8')
            crawler.info(resp.headers)
        else:
            count -= 1
            return count
    except Exception as e:
        os.kill(os.getppid(), signal.SIGTERM)
        return str(e)
    return api_result

