def min_seconds_to_palindrome(gemstones):
    min_seconds = 1
    for gemstone in gemstones:
        (left, right) = (0, len(gemstone) - 1)
        while left < right:
            if gemstone[left] != gemstone[right]:
                min_seconds += 1
                right -= 1
            else:
                left += 1
                right -= 1
    return min_seconds

