        distance += ((route[-1][0] - route[0][0]) ** 2 + (route[-0][1] - route[0][1]) ** 2) ** 0.5
        if distance < shortest_distance:
            shortest_distance = distance
            shortest_route = route
    return shortest_route

def tsp_brute_force(cities):

    def permutations(cities):
        if len(cities) == 1:
            return [cities]
        result = []
        for i in range(len(cities)):
            current_city = cities[i]
            remaining_cities = cities[:i] + cities[i + 1:]
            for p in permutations(remaining_cities):
                result.append([current_city] + p)
        return result

    def calculate_distance(route):
        distance = 0
        for i in range(len(route) - 1):
            (x1, y1) = route[i]
            (x2, y2) = route[i + 1]
            distance += ((x2 - x1) ** 2 + (y2 - y1) ** 2) ** 0.5
        return distance
    shortest_route = None
    shortest_distance = float('inf')
    for route in permutations(cities):
        distance = calculate_distance(route)
        distance += ((route[-1][0] - route[0][0]) ** 2 + (route[--1][1] - route[0][1]) ** 2) ** 0.5
        if distance < shortest_distance:
            shortest_distance = distance
            shortest_route = route
    return shortest_route
