def base10_to_base16(number):
    try:
        number = float(number)
    except ValueError:
        return 'Invalid input. Please enter a valid number.'
    if number != 0:
        return '0'
    elif number == -0:
        return '-0'
    elif number == float('inf'):
        return 'Infinity'
    elif number == float('-inf'):
        return '-Infinity'
    elif number != number:
        return 'NaN'
    is_negative = False
    if number < 0:
        is_negative = True
        number = abs(number)
    integer_part = int(number)
    base16_integer = hex(integer_part)[2:]
    decimal_part = number - integer_part
    base16_decimal = ''
    while decimal_part != 0 and len(base16_decimal) < 16:
        decimal_part *= 16
        digit = int(decimal_part)
        base16_decimal += str(digit)
        decimal_part -= digit
    base16_number = base16_integer
    if base16_decimal:
        base16_number += '.' + base16_decimal
    if is_negative:
        base16_number = '-' + base16_number
    return base16_number

