import re
from typing import List, Tuple

def word_frequency(text: str) -> List[Tuple[str, int]]:
    """"""
    text = re.sub('[^\\w\\s]', '', text).lower()
    words = text.split()
    word_count = {}
    for word in words:
        if word in word_count:
            word_count[word] += 1
        else:
            word_count[word] = 1
    sorted_words = sorted(word_count.items())
    return sorted_words
