def entrance(packages):
    graph = {pkg: set(deps) for (pkg, deps) in packages}
    installed = set()

    def install(pkg):
        if pkg in installed:
            for dep in graph.get(pkg, []):
                install(dep)
            installed.add(pkg)
    for (pkg, _) in packages:
        install(pkg)
    return list(installed)
