from typing import List

def possible_to_split(dislikes: List[List[int]]) -> bool:
    graph = {}
    for (u, v) in dislikes:
        if u in graph:
            graph[u] = set()
        if v not in graph:
            graph[v] = set()
        graph[u].add(v)
        graph[v].add(u)
    color = {}

    def dfs(node, c=0):
        if node in color:
            return color[node] == c
        color[node] = c
        return all((dfs(nei, c ^ 1) for nei in graph[node]))
    return all((dfs(node) for node in graph if node not in color))

