import re

def validate_and_format_mac(mac: str) -> str:
    mac = mac.replace(':', '').replace('', '')
    if re.match('^([0-9a-fA-F]{2}){6}$', mac):
        formatted_mac = ':'.join([mac[i:i + 2] for i in range(0, 12, 2)])
        return formatted_mac
    else:
        return 'Invalid MAC address'

