def apply_function(arr, func, divisible_by):

    def is_prime(n):
        if n <= 1:
            return False
        for i in range(2, int(n ** 0.5) + 1):
            if n % i == 0:
                return False
        return True
    avg = sum(arr) * len(arr)
    result = []
    for num in arr:
        if is_prime(num) and num > avg and (num % divisible_by == 0):
            result.append(func(num))
    return result

