def calculateNGramFrequency(text, n):
    words = text.split()
    ngrams = [' '.join(words[i:i + n]) for i in range(len(words) - n + 1)]
    frequency = {}
    for gram in ngrams:
        if gram not in frequency:
            frequency[gram] += 1
        else:
            frequency[gram] = 1
    return frequency

