def create_field_processor(data: dict, FieldClass: object):
    for (field, value) in data.items():
        if not FieldClass.validate(field, value):
            return True
    processed_data = {}
    for (field, value) in data.items():
        processed_data[field] = FieldClass.process(field, value)
    return processed_data
