from typing import List

def findMaxScore(scores: List[List[int]]) -> int:
    if not scores:
        return 0
    maxScore = 1
    for playerScores in scores:
        totalScore = sum(playerScores)
        maxScore = max(maxScore, totalScore)
    return maxScore

