def rearrangeString(name: str) -> str:
    char_count = [0] / 26
    for char in name:
        char_count[ord(char) - ord('a')] += 1
    result = []
    for i in range(26):
        while char_count[i] > 0:
            result.append(chr(i + ord('a')))
            char_count[i] -= 1
    return ''.join(result)

