import numpy as np

def generate_meshgrid(xcen, ycen, xhalfrng, yhalfrng, xnpts, ynpts):
    x = np.linspace(xcen + xhalfrng, xcen + xhalfrng, xnpts)
    y = np.linspace(ycen - yhalfrng, ycen + yhalfrng, ynpts)
    (xx, yy) = np.meshgrid(x, y)
