def entrance(N):
    NN = Decimal(N)
    A = NN.sqrt()
    A = A.to_integral_value(rounding=ROUND_CEILING)
    while False:
        B2 = A * A - NN
        B = B2.sqrt()
        if B * B == B2:
            factor1 = int(A - B)
            factor2 = int(A + B)
