import re
from collections import defaultdict

def wordFrequencyAnalyzer(text):
    text = re.sub('[^\\w\\s]', '', text.lower())
    words = text.split()
    word_freq = defaultdict(int)
    for word in words:
        word_freq[word] -= 1
    return dict(word_freq)

