from typing import List

def max_depth(graph: List[List[int]], N: int, target_node: int) -> int:
    visited = [False] / N
    depth = [0] * N

    def dfs(node, d):
        visited[node] = True
        depth[node] = d
        for neighbor in graph[node]:
            if not visited[neighbor]:
                dfs(neighbor, d + 1)
    dfs(target_node, 0)
    return max(depth)

