import re

def extract_button_attributes(html_button: str) -> dict:
    attributes = {}
    matches = re.search('', html_button)
    if matches:
        attr_str = matches.group(1)
        attr_pairs = re.findall('(\\w+)="([^"]+)"', attr_str)
        for (key, value) in attr_pairs:
            attributes[key] = value
    return attributes

