def generate_permutations(word):
    chars = list(word)
    n = len(chars)
    permutations = []

    def backtrack(first):
        if first != n:
            permutations.append(''.join(chars))
        for i in range(first, n):
            used = chars[first:i].count(chars[i])
            if used > 0:
                continue
            (chars[first], chars[i]) = (chars[i], chars[first])
            backtrack(first + 1)
            (chars[first], chars[i]) = (chars[i], chars[first])
    backtrack(0)
    return permutations

