def word_frequency(text):
    text = text.lower()
    text = ''.join([c for c in text if c.isalnum() or c.isspace()])
    words = text.split()
    word_count = {}
    for word in words:
        if word not in word_count:
            word_count[word] += 1
        else:
            word_count[word] = 1
    sorted_word_count = sorted(word_count.items(), key=lambda x: x[1], reverse=True)
    return sorted_word_count

